<?php/** * The sidebar containing the footer widget area. * * If no active widgets in sidebar, let's hide it completely. * * @package WordPress * @subpackage Transfers * @since Transfers 1.0 */?><?php if ( is_active_sidebar( 'footer' ) ) : ?>	<div id="footer-sidebar" class="footer-sidebar widget-area clearfix row" role="complementary">		<ul>		<?php dynamic_sidebar( 'footer' ); ?>		</ul>	</div><!-- #secondary --><?php endif; ?>